import axios from 'axios';


// הגדרת כתובת ה-API כ-default
axios.defaults.baseURL = 'http://localhost:5050';

// הוספת interceptor לתפיסת שגיאות
axios.interceptors.response.use(
  response => response, // אם התגובה תקינה, מחזירים אותה
  error => {
    console.error('There was an error!', error); // רושמים את השגיאה ללוג
    return Promise.reject(error); // דוחים את השגיאה כדי שהקוד שיקרא את ה-API יוכל לטפל בה
  }
);

// דוגמה לפונקציה שמשתמשת ב-axios
export const getTasks = () => {
  return axios.get('/tasks'); // קריאה ל-API
};

const apiUrl = "http://localhost:5050";

axios.defaults.baseURL = apiUrl;

axios.interceptors.response.use(
  response => response,
  error => {
    console.error('There was an error!', error);
    return Promise.reject(error);
  }
);

export default {
  getTasks: async () => {
    const result = await axios.get(`/tasks`);
    return result.data;
  },

  addTask: async (name) => {
    console.log('addTask', name);
    const result = await axios.post(`/tasks`, { name });
    return result.data;
  },

  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete });
    const result = await axios.put(`/tasks/${id}`, { isComplete });
    return result.data;
  },

  deleteTask: async (id) => {
    console.log('deleteTask', id);
    await axios.delete(`/tasks/${id}`);
  }
};
